import React from "react";
import { Link } from "react-router-dom";
import './css/Navigation.css';

class Navigation extends React.Component {
    componentDidMount(){
        this.textEffect();
    }

    textEffect(){
        let possibleCharacters = ['$','#','@','!','%','&','유','니','텍','오','토']
        let endingCharacters = ['유','니','텍','오','토']
        console.log(document.getElementById('name').children)
        let parentElement = document.getElementById('name').children
        for(let i=0; i< parentElement.length;i++){
            let maxTime = (i*300) + 1000
            let currentTime = 0
            //console.log(parentElement[i])
            while(currentTime < maxTime){
                console.log('testing')
                let randomNumber = Math.floor(Math.random() * 11);
                setTimeout(()=>{
                    parentElement[i].innerHTML = possibleCharacters[randomNumber];
                }, currentTime)
                currentTime += 50
            }
            setTimeout(()=>{
                parentElement[i].innerHTML = endingCharacters[i]
            }, maxTime + 100)
        }
    }

    render() {
        return (
            <div id='navigation'>
                <Link id='name' className="links" to="/"><span>유</span><span>니</span><span>텍</span><span>오</span><span>토</span></Link>
                <div id='nav-links'>
                    <Link className="links" to="/About">회사소개</Link>
                    <Link className="links" to="/About">문의</Link>
                    <Link className="links" to="/About">오시는 길</Link>
                </div>
            </div>
        )
    }
}

export default Navigation;
